import React, {useState} from "react";
import { Button, Paper, styled, Typography } from "@mui/material";
import AddIcon from '@mui/icons-material/Add';
import ReactCrop, {
      centerCrop,
      makeAspectCrop,
      Crop,
      PixelCrop,
} from 'react-image-crop';
import canvasPreview from '../../utils/canvasPreview';
import useDebounceEffect from '../../utils/useDebounceEffect';
import 'react-image-crop/dist/ReactCrop.css';
import {Container} from "@mui/material";
import { useRef } from "react";

const UploadPhotoCard = () => {
      const [imgSrc, setImgSrc] = useState('');
      const previewCanvasRef = useRef<HTMLCanvasElement>(null);
      const imgRef = useRef<HTMLImageElement>(null);
      const [crop, setCrop] = useState<Crop>({});
      const [completedCrop, setCompletedCrop] = useState<PixelCrop>({});
      const [aspect, setAspect] = useState(16/9);


      const onSelectFile = (e) => {
            if (e.target.files && e.target.files.length > 0) {
                  setCrop(undefined) // Makes crop preview update between images.
                  const reader = new FileReader()
                  reader.addEventListener('load', () =>
                        setImgSrc(reader.result?.toString() || ''),
                  )
                  reader.readAsDataURL(e.target.files[0])
            }
      }

      const onImageLoad = (e) => {
            if (aspect) {
                  const { width, height } = e.currentTarget
                  setCrop(centerAspectCrop(width, height, aspect))
            }
      }

      useDebounceEffect(
            async () => {
              if (
                completedCrop?.width &&
                completedCrop?.height &&
                imgRef.current &&
                previewCanvasRef.current
              ) {
                // We use canvasPreview as it's much faster than imgPreview.
                canvasPreview(
                  imgRef.current,
                  previewCanvasRef.current,
                  completedCrop
                )
              }
            },
            100,
            [completedCrop],
      )

      const centerAspectCrop = (
            mediaWidth,
            mediaHeight,
            aspect,
          ) =>{
            return centerCrop(
              makeAspectCrop(
                {
                  unit: '%',
                  width: 90,
                },
                aspect,
                mediaWidth,
                mediaHeight,
              ),
              mediaWidth,
              mediaHeight,
            )
          }


      const PaperButton = styled(Paper) ({
            elevation: "2",
            display: 'flex',
            flexWrap: 'wrap',
            width: 220,
            height: 220,
            justifyContent: 'center',
            alignItems: 'center',
            borderRadius: '10px',
            border: '2px color #666',
            "&:hover": {
                  backgroundColor: "rgb(100,100,100,0.8)",
                  "& svg": {
                        color: "rgb(243, 116, 231, 0.1)"
                  },
                  "& p": {
                        color: 'white',
                        display: 'block'
                  }
            },
            position: 'relative'

      })

      const handleUploadCardClick = () => {

      }

      return(
            <Container>
            <Button component="label" onClick={handleUploadCardClick}>
                  <PaperButton>
                        <AddIcon sx={{fontSize: '8em', color: '#F374E7'}}/>
                        <Typography variant="h10" component="p" hidden style={{
                              position: 'absolute',
                              bottom: 0
                        }}>
                              Haz un clic aqu√≠ para subir una foto
                        </Typography>
                  </PaperButton>
                  <input hidden accept="image/*" multiple type="file" onChange={onSelectFile}/>
            </Button>
            {!!imgSrc && (
                  <ReactCrop
                    crop={crop}
                    onChange={(_, percentCrop) => setCrop(percentCrop)}
                    onComplete={(c) => setCompletedCrop(c)}
                    aspect={aspect}
                  >
                    <img
                      ref={imgRef}
                      alt="Crop me"
                      src={imgSrc}
                      onLoad={onImageLoad}
                    />
                  </ReactCrop>
                )}
            <div>
                  {!!completedCrop && (
                    <canvas
                      ref={previewCanvasRef}
                      style={{
                        border: '1px solid black',
                        objectFit: 'contain',
                        width: completedCrop.width,
                        height: completedCrop.height,
                      }}
                    />
                  )}
            </div>

            </Container>
      );
}

export default UploadPhotoCard;
