import React, { useState, useEffect } from 'react';
import Paper from '@mui/material/Paper';
import { Radio } from '@mui/material';
import {RadioGroup, FormControlLabel} from '@mui/material';
import { useDispatch, useSelector } from 'react-redux';
import { setProduct } from '../../features/productSelection/ProductSlice';


const ProductCard = ({title, productName, formData, setFormData}) => {   
      const dispatch = useDispatch();
      const selectedProduct = useSelector(state => state.product);
      const [isChecked, setChecked] = useState(false);

      const changeHandler = (e) => {
            dispatch(setProduct(e.target.value));
            setFormData({
                  ...formData,
                  enableNextButton: true
            })
      }

      useEffect(() => {
            (selectedProduct === productName) ? setChecked(true) : setChecked(false)
      }, [formData])

      return (
            <Paper elevation={3} sx={{
                  display: 'flex',
                  flexWrap: 'wrap',
                  width: 280,
                  height: 280
            }}>
                  {title}

                  <RadioGroup
                        row
                        aria-labelledby="chooseProduct-sameSize"
                        name="chooseProduct"
                        onChange={changeHandler}
                  >
                        <FormControlLabel checked={isChecked} value={productName} control={<Radio />} />
                  </RadioGroup>
            </Paper>
  )
}

export default ProductCard;
