import React, {useEffect, useState} from "react";
import { Box, Button, Paper, styled, Typography } from "@mui/material";
import AddIcon from '@mui/icons-material/Add';
import {Container} from "@mui/material";
import UploadPhotoDialog from "../uploadPhotoDialog/uploadPhotoDialog.component";
import UploadPhotoCardEmpty from "../uploadPhotoCardEmpty/uploadPhotoCardEmpty.component";
import UploadPhotoCardHover from "../uploadPhotoCardHover/uploadPhotoCardHover.component";
import { useSelector } from "react-redux";


const UploadPhotoCard = ({id, imgSrc, formData, setFormData, handleDeleteCard}) => {

      const [dialogOpen, setDialogOpen] = useState(false);
      const handleOpen = () => {
            setDialogOpen(true);
      }
      const handleClose = () => {
            setDialogOpen(false);
      }
      //const [emptyCardHidden, setEmptyCardHidden] = useState(false);

      const currentPhoto = useSelector(state => state.photos.find(photo => photo.id === id));
      const emptyCardHidden = currentPhoto.imgSrc !== null;

      return(
            <Container>
                  <Button component="label" position="relative" sx={{
                        padding: 0,
                        margin: 0,
                  }}>
                        {
                              !emptyCardHidden ? (<UploadPhotoCardEmpty id={id} sx={{
                                    position: 'absolute',
                                    top: 0,
                                    left: 0,
                              }} />
                              ): null 
                        }
                        {
                              emptyCardHidden ? (<UploadPhotoCardHover id={id} handleOpen={handleOpen} sx={{
                              position: 'absolute',
                              top: 0,
                              left: 0,
                              }}/>
                              ): null
                        }
                        {//<UploadPhotoDialog handleClose={handleClose} />
                        }
                  </Button>

            </Container>
      );
}

export default UploadPhotoCard;
