import React, {useState} from "react";
import { Button, Paper, styled, Typography } from "@mui/material";
import AddIcon from '@mui/icons-material/Add';
import ReactCrop, {
      centerCrop,
      makeAspectCrop,
      Crop,
      PixelCrop,
} from 'react-image-crop';
import canvasPreview from '../../utils/canvasPreview';
import useDebounceEffect from '../../utils/useDebounceEffect';
import 'react-image-crop/dist/ReactCrop.css';
import {Container} from "@mui/material";
import { useRef } from "react";

const UploadPhotoCard = () => {
      const [src, setSrc] = useState(null);
      const [crop, setCrop] = useState({ aspect: 16 / 9 });
      const [image, setImage] = useState(null);
      const [output, setOutput] = useState(null);

      const selectImage = (file) => {
            setSrc(URL.createObjectURL(file));
      };

      const cropImageNow = () => {
            const canvas = document.createElement('canvas');
            const scaleX = image.naturalWidth / image.width;
            const scaleY = image.naturalHeight / image.height;
            canvas.width = crop.width;
            canvas.height = crop.height;
            const ctx = canvas.getContext('2d');

            const pixelRatio = window.devicePixelRatio;
            canvas.width = crop.width * pixelRatio;
            canvas.height = crop.height * pixelRatio;
            ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);
            ctx.imageSmoothingQuality = 'high';

            ctx.drawImage(
              image,
              crop.x * scaleX,
              crop.y * scaleY,
              crop.width * scaleX,
              crop.height * scaleY,
              0,
              0,
              crop.width,
              crop.height,
            );

            // Converting to base64
            const base64Image = canvas.toDataURL('image/jpeg');
            setOutput(base64Image);
      };

      const onSelectFile = (e) => {
            selectImage(e.target.files[0]);
      }

      const PaperButton = styled(Paper) ({
            elevation: "2",
            display: 'flex',
            flexWrap: 'wrap',
            width: 220,
            height: 220,
            justifyContent: 'center',
            alignItems: 'center',
            borderRadius: '10px',
            border: '2px color #666',
            "&:hover": {
                  backgroundColor: "rgb(100,100,100,0.8)",
                  "& svg": {
                        color: "rgb(243, 116, 231, 0.1)"
                  },
                  "& p": {
                        color: 'white',
                        display: 'block'
                  }
            },
            position: 'relative'

      })

      const handleUploadCardClick = () => {

      }

      return(
            <Container>
            <Button component="label" onClick={handleUploadCardClick}>
                  <PaperButton>
                        <AddIcon sx={{fontSize: '8em', color: '#F374E7'}}/>
                        <Typography variant="h10" component="p" hidden style={{
                              position: 'absolute',
                              bottom: 0
                        }}>
                              Haz un clic aqu√≠ para subir una foto
                        </Typography>
                  </PaperButton>
                  <input hidden accept="image/*" multiple type="file" onChange={onSelectFile}/>
            </Button>


            </Container>
      );
}

export default UploadPhotoCard;
