import React, { useEffect } from "react";
import { Box, IconButton, styled } from "@mui/material";
import CropIcon from '@mui/icons-material/Crop';
import DeleteIcon from '@mui/icons-material/Delete';
import { useSelector } from "react-redux";

const UploadPhotoCardHover = ({ id, imgResult, handleOpen }) =>{      
      const UploadPhotoCardHoverButton = styled(IconButton) ({
            width: 30,
            height: 30,
            color:"primary",
            ariaLabel:"Crop Photo",
            backgroundColor: 'white',
            borderRadius: '3px',
      });

      /*const getCorrespondingCard = () => {
            const cardId = newUploadPhotoCard.id;
            const cardImgSrc = newUploadPhotoCard.imgSrc;
            handleDeleteCard(cardId, cardImgSrc);
      }*/
      //const {imgSrc} = useSelector(state => state.photos.find(photo => photo.id === id));
      console.log("id: ", id);
      const currentImgSrc = useSelector(state=>state.photos[id]);
      console.log("currentImgSrc", currentImgSrc);
      return(
            <Box sx={{
                  width: 220,
                  height: 220,
                  position: 'relative'
            }}>
                  <img width={220} height={220} src={currentImgSrc} alt="" style={{borderRadius: '10px'}}/>
                  <UploadPhotoCardHoverButton component="label" onClick={handleOpen} sx={{
                        position: 'absolute',
                        top: 10,
                        right: 10,
                  }}>
                        <CropIcon sx={{
                              width: 20
                        }}/>
                  </UploadPhotoCardHoverButton>
                  <UploadPhotoCardHoverButton component="label" onClick={()=>{}} sx={{
                        position: 'absolute',
                        top: 45,
                        right: 10,
                  }}>
                        <DeleteIcon sx={{
                              width: 20
                        }}/>
                  </UploadPhotoCardHoverButton>
            </Box>
      );
}

export default UploadPhotoCardHover;
