import React, {useEffect, useState} from "react";
import { Container, Box, InputLabel, Select, MenuItem, Input, Button } from "@mui/material";
import { Fragment } from "react";
import { useDispatch } from "react-redux";
import { nextButtonEnabled } from "../../features/handleFormButtons/FormButtonsSlice";
import lightWoodBackground from '../../images/lightWood.png';
import darkWoodBackground from '../../images/darkWood.png';
import { customPhraseAdded, defaultPhraseAdded } from "../../features/additionalPhrase/AdditionalPhraseSlice";
import * as Yup from 'yup';

import { Formik, Field, Form, ErrorMessage } from 'formik';

const AddPhrase = () => {
      const defaultPhraseColor = 'lightWood';
      return(
            <div>
                  <h1>Add Phrase</h1>
                  <Formik
                        initialValues={{phraseType: '', phraseText: '', phraseColor: defaultPhraseColor}}
                        validationSchema={Yup.object({
                              phraseType: Yup.string().oneOf(
                                    ['family', 'friends', 'custom'],
                                    'Phrase required'
                              ).required('Required'),
                              phraseText: Yup.string().when("phraseType", {
                                    is: ((phraseType) => ['family', 'friends', 'custom'].includes(phraseType)),
                                    then: Yup.string().required('Required'),
                              }),
                              phraseColor: Yup.string().when("phraseType", {
                                    is: ((phraseType) => ['family', 'friends', 'custom'].includes(phraseType)),
                                    then: Yup.string().oneOf(
                                          ['lightWood', 'darkWood'],
                                          'Color required'
                                    ).required('Required'),
                              }),
                        })}
                        onSubmit={(values, {setSubmitting}) => {
                              setTimeout(() => {
                                    alert(JSON.stringify(values, null, 2));
                                    setSubmitting(false);
                              }, 400);
                        }}
                        >
                        <Form>
                              <Field name="phraseType" as="select">
                                    <option value="default">Choose a Phrase</option>
                                    <option value="family">Family</option>      
                                    <option value="friends">Friends</option>
                                    <option value="custom">Personalizado</option>
                              </Field>
                              <Field name="phraseText" type="text" placeholder="Choose Text..."/>
                              <Field name="phraseColor" as="select">
                                    <option value="lightWood">Light Wood</option>      
                                    <option value="darkWood">Dark Wood</option>
                              </Field>
                        </Form>
                  </Formik>
            </div>
      );
}

export default AddPhrase;
