import React from "react";
import { Box, IconButton, styled } from "@mui/material";
import CropIcon from '@mui/icons-material/Crop';

const UploadPhotoCardHover = ({formData, setFormData}) =>{
      const CropButton = styled(IconButton) ({
            width: 20,
            height: 20,
            position: 'absolute',
            top: 0,
            right: 0, 
            color:"primary",
            ariaLabel:"Crop Photo",
            backgroundColor: 'white',
            borderRadius: '3px',
            padding: '3px'
      });

      return(
            <Box sx={{
                  width: 220,
                  height: 220,
                  position: 'absolute',
                  top: 0,
                  left: 0
            }}>
                  <img width={220} height={220} src={formData.uploadedPhotos.slice(-1)} alt="uploadedPhoto" />
                  <CropButton component="label">
                        <CropIcon fontSize="0.2em"/>
                  </CropButton>
            </Box>
      );
}

export default UploadPhotoCardHover;
