import React, {useEffect, useState} from "react";
import { Box, Button, Paper, styled, Typography } from "@mui/material";
import AddIcon from '@mui/icons-material/Add';
import {Container} from "@mui/material";
import UploadPhotoDialog from "../uploadPhotoDialog/uploadPhotoDialog.component";
import UploadPhotoCardEmpty from "../uploadPhotoCardEmpty/uploadPhotoCardEmpty.component";


const UploadPhotoCard = ({formData, setFormData}) => {
      // image source
      const [srcImg, setSrcImg] = useState(null);
      // image for cropping
      const [image, setImage] = useState(null);
      // aspect ratio of crop tool
      const [crop, setCrop] = useState({aspect: 16/9});
      //save result
      const [result, setResult] = useState(null);

      return(
            <Container>
                  <Button component="label" position="relative">
                        <UploadPhotoCardEmpty formData={formData} setFormData={setFormData} />
                        <Box sx={{
                              width: 220,
                              height: 220,
                              position: 'absolute',
                              top: 0,
                              left: 0
                        }}>
                              <img width={220} height={220} src={formData.uploadedPhotos.slice(-1)} alt="uploadedPhoto" />
                        </Box>
                  </Button>

            </Container>
      );
}

export default UploadPhotoCard;
