import React, {useEffect, useState} from "react";
import { Container, Box, InputLabel, Select, MenuItem, TextField, Button } from "@mui/material";
import { Fragment } from "react";
import { useDispatch } from "react-redux";
import { nextButtonEnabled } from "../../features/handleFormButtons/FormButtonsSlice";
import lightWoodBackground from '../../images/lightWood.png';
import darkWoodBackground from '../../images/darkWood.png';
import { customPhraseAdded, defaultPhraseAdded } from "../../features/additionalPhrase/AdditionalPhraseSlice";
import * as yup from 'yup';
import { Formik, Field, Form, ErrorMessage, useFormik} from 'formik';
import {FormControl} from "@mui/material";

const AddPhrase = () => {
      const [isHidden, setHidden] = useState(true);
      const defaultPhraseColor = 'lightWood';
      const [selectedPhrase, setSelectedPhrase] = useState('');
      const [isDisabled, setDisabled] = useState(true);
      const dispatch = useDispatch();

      const handleSelectChange = (e) => {
            setSelectedPhrase(e.target.value);
            setDisabled(false);
      }

      useEffect(() => {
            (selectedPhrase === 'custom') ? setHidden(false) : setHidden(true);
            console.log("state of disabled: ", isDisabled);
      }, [selectedPhrase]);

      const validationSchema= yup.object({
            phraseText: yup.string().matches(/^(?!\s+$)[0-9a-zñáéíóúü]*/i)
                        .required('Required'),
            phraseColor: yup.string().oneOf(
                        ['lightWood', 'darkWood'],
                        'Color required'
                  ).required('Required'),
      });

      const formik = useFormik({
            initialValues:{phraseType: '', phraseText: '', phraseColor: defaultPhraseColor},
            onSubmit: (values, {setSubmitting}) => {
                  alert("YO");
                  /*setTimeout(() => {
                        values.phraseType = selectedPhrase;

                        if(values.phraseType === 'custom') {
                              dispatch(customPhraseAdded({
                                    text: values.phraseText,
                                    color: values.phraseColor
                              }));
                        } else {
                              // default chosen
                              dispatch(defaultPhraseAdded({
                                    defaultPhrase: values.phraseType
                              }));
                        }
                        alert(JSON.stringify(values, null, 2));
                        setSubmitting(false);
                  }, 400);*/
            }
      });

      return(
            <div>
                  <h1>Add Phrase</h1>
                  <form onSubmit={formik.onSubmit}>
                              <FormControl fullWidth>
                                    <InputLabel id="choosePhraseType">Choose a Phrase</InputLabel>
                                    <Select name="phraseType" value={selectedPhrase} labelId="choosePhraseType" onChange={handleSelectChange}>
                                          <MenuItem value="family">Family</MenuItem>      
                                          <MenuItem value="friends">Friends</MenuItem>
                                          <MenuItem value="custom">Personalizado</MenuItem> 
                                    </Select>
                              </FormControl>
                              { !isHidden ? 
                                    <Fragment>
                                          <TextField
                                                label="Choose Text..."
                                                variant="outlined"
                                                value={formik.values.phraseText}
                                                onChange={formik.handleChange}
                                                error={formik.touched.phraseText && Boolean(formik.errors.phraseText)}
                                                helperText={formik.touched.phraseText && formik.errors.phraseText}
                                          />

                                          <Select
                                                name="phraseColor"
                                                label="Choose Color"
                                                value={formik.values.phraseColor}
                                                onChange={formik.handleChange}
                                                error={formik.touched.phraseColor && Boolean(formik.errors.phraseColor)}
                                                helperText={formik.touched.phraseColor && formik.errors.phraseColor}
                                          >
                                                <option value="lightWood">Light Wood</option>      
                                                <option value="darkWood">Dark Wood</option>
                                          </Select>
                                    </Fragment>
                                    : null
                              }
                              <Button disabled={isDisabled} type="submit" variant="contained">Add Phrase</Button>
                        </form>
            </div>
      );
}

export default AddPhrase;
