import React, {useState} from "react";
import { Container, FormControl, InputLabel, Select, MenuItem, Input } from "@mui/material";

const AddPhrase = () => {
      const [phraseType, setPhraseType] = useState("");
      const [phraseColor, setPhraseColor] = useState("");
      const [phraseText, setPhraseText] = useState("");

      const handleChangeType = (event) => {
            setPhraseType(event.target.value);
      };

      const handleChangeColor = (e) => {
            setPhraseColor(event.target.value);
      }

      return(
      <Container>
            <h1>Add Phrase</h1>
            <FormControl fullWidth>
                  <InputLabel id="addPhraseLabel">Choose Phrase</InputLabel>
                  <Select
                  labelId="addPhraseLabel"
                  value={phraseType}
                  label="Phrase Type"
                  onChange={handleChangeType}
                  >
                        <MenuItem value={"family"}>Family</MenuItem>
                        <MenuItem value={"friends"}>Friends</MenuItem>
                        <MenuItem value={"personalized"}>Personalizado</MenuItem>
                        <Input placeholder="Your text" />
                        <Select value={phraseColor} onChange={handleChangeColor}>
                              <MenuItem value={"Brown"}>Brown</MenuItem>
                              <MenuItem value={"Woord"}>Brown</MenuItem>
                        </Select>
                  </Select>
            </FormControl>
      </Container>
      );
}

export default AddPhrase;
