import { createSlice, nanoid } from "@reduxjs/toolkit";

const initialState = [
      {id: null, imgSrc: null, imgResult: null, imgDimensions: null}
]

const photoSlice = createSlice({
      name: 'photos',
      initialState,
      reducers: {
            photoAdded: {
                  reducer(state, action) {
                        state.push(action.payload)
                  },
                  prepare(imgSrc, imgResult, imgDimensions) {
                        return {
                              payload: {
                                    id: nanoid(),
                                    imgSrc,
                                    imgResult,
                                    imgDimensions
                              }
                        }
                  }
            }
            // one to update
      }
})

export const {photoAdded} = photoSlice.actions;


export default photoSlice.reducer;
