import React, { } from "react";
import { Box, IconButton, styled } from "@mui/material";
import CropIcon from '@mui/icons-material/Crop';
import DeleteIcon from '@mui/icons-material/Delete';
import UploadPhotoDialog from "../uploadPhotoDialog/uploadPhotoDialog.component";

const UploadPhotoCardHover = ({ newUploadPhotoCard, setNewUploadPhotoCard}) =>{      
      const [open, setOpen] = useState(false);

      const UploadPhotoCardHoverButton = styled(IconButton) ({
            width: 30,
            height: 30,
            color:"primary",
            ariaLabel:"Crop Photo",
            backgroundColor: 'white',
            borderRadius: '3px',
      });

      const handleCrop = () => {
            setOpen(true);
      }

      return(
            <Box sx={{
                  width: 220,
                  height: 220,
                  position: 'relative'
            }}>
                  <img width={220} height={220} src={newUploadPhotoCard.imgSrc} alt="" style={{borderRadius: '10px'}}/>
                  <UploadPhotoCardHoverButton component="label" onclick={handleCrop} sx={{
                        position: 'absolute',
                        top: 10,
                        right: 10,
                  }}>
                        <CropIcon sx={{
                              width: 20
                        }}/>
                  </UploadPhotoCardHoverButton>
                  <UploadPhotoCardHoverButton component="label" sx={{
                        position: 'absolute',
                        top: 45,
                        right: 10,
                  }}>
                        <DeleteIcon sx={{
                              width: 20
                        }}/>
                  </UploadPhotoCardHoverButton>
                  <UploadPhotoDialog open={open} />
            </Box>
      );
}

export default UploadPhotoCardHover;
