import React, {useEffect, useState} from "react";
import { Box, Button, Paper, styled, Typography } from "@mui/material";
import AddIcon from '@mui/icons-material/Add';
import {Container} from "@mui/material";
import UploadPhotoDialog from "../uploadPhotoDialog/uploadPhotoDialog.component";
import UploadPhotoCardEmpty from "../uploadPhotoCardEmpty/uploadPhotoCardEmpty.component";
import UploadPhotoCardHover from "../uploadPhotoCardHover/uploadPhotoCardHover.component";

const UploadPhotoCard = ({formData, setFormData}) => {
      const [isHidden, setHidden] = useState(false);
      const [newUploadPhotoCard, setNewUploadPhotoCard] = useState({
            srcImg: null,
            result: null
      });

      useEffect(() => {
            newUploadPhotoCard.srcImg !== null ? setHidden(true) : setHidden(false);
            console.log("srcImg from photocard:", newUploadPhotoCard.srcImg);
      }, [newUploadPhotoCard]);

      return(
            <Container>
                  <Button component="label" position="relative" sx={{
                        padding: 0,
                        margin: 0,
                  }}>
                        {
                              !isHidden ? (<UploadPhotoCardEmpty newUploadPhotoCard={newUploadPhotoCard} setNewUploadPhotoCard={setNewUploadPhotoCard} sx={{
                                    position: 'absolute',
                                    top: 0,
                                    left: 0,
                              }} />
                              ): null 
                        }
                        <UploadPhotoCardHover newUploadPhotoCard={newUploadPhotoCard} setNewUploadPhotoCard={setNewUploadPhotoCard} sx={{
                              position: 'absolute',
                              top: 0,
                              left: 0,
                        }}/>
                  </Button>

            </Container>
      );
}

export default UploadPhotoCard;
