import { Button, Box, Container, Grid, Typography } from '@mui/material';
import React, {useState, useEffect} from 'react';
import UploadPhotoCard from '../../uploadPhotoCard/uploadPhotoCard.component';

const SameSize = ({formData, setFormData}) => {
  const [uploadCards, setUploadCards] = useState([]);

  const handleAddCard = () => {
    setUploadCards([
      ...uploadCards,
      <UploadPhotoCard formData={formData} setFormData={setFormData} handleDeleteCard={handleDeleteCard} />
    ]);
  }

  const handleDeleteCard = (e) => {
    // remove card from uploadCards state
    setUploadCards([
      ...uploadCards
    ]);
    // remove related img from formData uploadedPhotos

  }

  return (
    <Box sx={{width: '100%'}}>
      <Typography variant="h1" component="h1">Mismo Tama√±o</Typography>
        <Button onClick={handleAddCard}>
          Add Card
        </Button>
        <Grid container columnSpacing={2} sx={{
          justifyContent: 'center',
        }}>
          {
            uploadCards.map((card, key) => {
              return(
                <Grid key={key} item xs={3} sm={3} md={3} lg={3} xl={3}>
                {card}
                </Grid>
              );
            })
          }
        </Grid>

    </Box>
  )
}

export default SameSize;
